        int n = score.size();

        vector<pair<int, int>> arr;
        for (int i=0; i<n; i++) arr.push_back({score[i], i});

        sort(arr.rbegin(), arr.rend());

        vector<string> res(n);
        for (int i=0; i<n; i++) {
            if (i == 0) {
                res[arr[i].second] = "Gold Medal";
            } else if (i == 1) {
                res[arr[i].second] = "Silver Medal";
            } else if (i == 2) {
                res[arr[i].second] = "Bronze Medal";
            } else {
                res[arr[i].second] = to_string(i+1);
            }
        }

        return Report(res);
    }

    vector<string> Report(vector<string>& res) {
        vector<string> result;
        for(int i=0; i<res.size(); i++) 
            result.push_back("Rank " + to_string(i+1) + ": " + res[i]);

        return result;
