               if(node->right){
                   q.push(node->right);
               }
            }
            n = tmp.size();
            for(i = 0; i < n/2; i++){  //fix
                z = tmp[i]->val;
                tmp[i]->val = tmp[n-1-i]->val;
                tmp[n-1-i]->val = z;
            }
            x ^= 1;
        }
        return root;
    }
};

