                int p2 = (i+1)*m + j;  // 1
                int p3 = i*m + (j+1);  // 2
                int p4 = i*m + (j-1);  // 2

                if(grid[i][j] == 1){
                    if(j+1 < m && (grid[i][j+1] == 1 || grid[i][j+1] == 3 || grid[i][j+1] == 5) ){
