        vector<int> v;
        for (int i=1; i<=n; i++)v.push_back(i);
        int i=0;
        while (v.size() != 1){
            i= (i+k-1)%(v.size());
            //cout<<v[i]<<endl;
            v.erase(v.begin()+i);
        }
        return v[0];
    }
};

