                    else if(x.first=='r')
                    {
                        for(auto j:blues[x.second])
                        {
                            visit.push({'b',j});
                            auto itr1=blues[x.second].find(j);
                            blues[x.second].erase(itr1);
                        }
                    }
                }
                if(justbreak)
                {
                    break;
                }
                steps++;
            }
            if(i==ans.size())
            {
                ans.push_back(-1);
            }
        }
        return ans;
    }
};

