public:
    vector<int> shortestAlternatingPaths(int n, vector<vector<int>>& redEdges, vector<vector<int>>& blueEdges) {
        vector<unordered_set<int>> redChilds(n), blueChilds(n);
        for (const vector<int>& edge : redEdges) {
            redChilds[edge[0]].insert(edge[1]);
        }
        for (const vector<int>& edge : blueEdges) {
            blueChilds[edge[0]].insert(edge[1]);
        }
        vector<int> ans(n, -1);
        ans[0] = 0;
        BFS(n, redChilds, blueChilds, ans, 0);
        BFS(n, blueChilds, redChilds, ans, 1);
        return ans;
    }

    void BFS(int n, const vector<unordered_set<int>>& curr, const vector<unordered_set<int>>& other, vector<int>& ans, int color) {
        queue<int> q;
        for (int child : curr[0]) {
            q.push(child);
            ans[child] = 1;
        }
        while (!q.empty()) {
            int node = q.front();
            q.pop();
            for (int child : other[node]) {
                if (ans[child] == -1) {
                    q.push(child);
                    ans[child] = ans[node] + 1;
                }
            }
        }
    }
};

