            visit.push({'n',0});//neutral='n'
            int steps=0;
            while(!visit.empty())
            {
                bool justbreak=false;
                int n=visit.size();
                for(int j=0;j<n;j++)
                {
                    pair<char,int>x=visit.front();
                    visit.pop();
                    if(x.second==i)
                    {
                        ans.push_back(steps);
                        justbreak=true;
                        break;
                    }
                    if(x.first=='n')
                    {
                        for(auto j:reds[x.second])
                        {
                            visit.push({'r',j});
                            auto itr1=reds[x.second].find(j);
