                        continue;

                    int val = grid[newi][newj];
                    if (val == '#')
                        continue;

                    if ('A' <= val && val <= 'F' && (key & (1 << (val - 'A'))) == 0)
                        continue;

                    if (mapKey[newi][newj] & (1l << key))
                        continue;

                     mapKey[newi][newj] |= 1l << (key+1);  
                     que.emplace(pair<int, int>(newi, newj), key);
                }
            }
            step++;
        }
        return -1;
    }
};

