class Solution {
public:
    vector<int> shortestAlternatingPaths(int n, vector<vector<int>>& redEdges, vector<vector<int>>& blueEdges) {
        vector<unordered_set<int>> redchilds(n), bluechilds(n);
        for (auto& edge : redEdges) {
            redchilds[edge[0]].insert(edge[1]);
        }
        for (auto& edge : blueEdges) {
            bluechilds[edge[0]].insert(edge[1]);
        }
        vector<int> ans(n, -1);
        queue<pair<int, int>> q;
        q.push({0, 0}); // {node, color}, color = 0 for red, color = 1 for blue
        vector<vector<bool>> visited(n, vector<bool>(2));
        visited[0][0] = true;
        visited[0][1] = true;
        int steps = 0;
        while (!q.empty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                auto [node, color] = q.front();
                q.pop();
                if (ans[node] == -1) {
                    ans[node] = steps;
                }
                if (color == 0) { // current color is red, find blue edges
                    for (auto next : bluechilds[node]) {
                        if (!visited[next][1]) {
                            q.push({next, 1});
                            visited[next][1] = true;
                        }
                    }
                } else { // current color is blue, find red edges
                    for (auto next : redchilds[node]) {
                        if (!visited[next][0]) {
                            q.push({next, 0});
                            visited[next][0] = true;
                        }
                    }
                }
            }
            steps++;
        }
        return ans;
    }
};

