class Solution {
public:

    // modified the function to make it return void instead of bool
    void valid(int n, string s,int sum, bool& isValid){
        // empty string indicates we have completed one partition
        if(s=="") {
            if(sum==n) isValid = true;
            return;
        }

        // We use backtracking to check for all partitions if it is possible to generate required sum 
        // and if we find sum we immediately return true immediately
        // removed the unclosed string declaration missing closing quotation marks
        for(int i=1;i<=s.size();i++){
            int x  = stoi(s.substr(0,i));
            valid(n, s.substr(i), sum+x, isValid);
            if(isValid) return;
        }
    }
    int punishmentNumber(int n) {
        int ans = 0;

        //generate all possible squares and check them if they satisfy the given statement
        for(int i=1;i<=n;i++){
            int sq = i*i;
            string x = to_string(sq);
            bool isValid = false;
            valid(i, x, 0, isValid);
            if(isValid) ans += i*i;
        }

        return checkAdditionalCondition(ans);
    }
};

