class Solution {
public:
    unordered_map<long long int,long long int>mp;
    int trailingZeroes(int n) {
        fun(n);
        return min(mp[2], mp[5]);
    }

    void fun(int n) {
        for (int i = 2; i <= n; i++) {
            int num = i;
            while (num % 2 == 0) {
                mp[2]++;
                num /= 2;
            }
            while (num % 5 == 0) {
                mp[5]++;
                num /= 5;
            }
        }
    }
};

