class Solution {
public:
    int closetTarget(vector<string>& words, string target, int startIndex) {
         int ind = -1;
         for (int i = 0; i < words.size(); i++) {
            if (words[i] == target) {
                ind = i;
                break;
            }
        }

        int n = words.size();
        if (ind == -1) return -1;
        if (startIndex == ind) return 0;

        int i = (startIndex - 1 + n) % n;
        int j = (startIndex + 1) % n;
        int cnt1 = 1;
        int cnt2 = 1;

        while ((i >= 0 || j < n)) {
            if (i >= 0 && words[i] == target) {
                return cnt1;
            }

            if (j < n && words[j] == target) {
                return cnt2;
            }

            cnt1++;
            cnt2++;
            i = (i - 1 + n) % n;
            j = (j + 1) % n;
        }

        return calculateDistance(target, n);
    }
};

