       return dp[index][prev] = max(op1 , op2);
   }

   int SolveByTab(string &s , int &k )
   {
        vector<vector<int>> dp(s.length()+1 , vector<int> (27, 0));

        for(int index = s.length()-1 ; index>=0 ; index--)
        {
            for(int prev = 0 ; prev<= 26 ; prev++)
            {
                int op1 = 0 + dp[index+1][prev];
                int op2 = 0 ;

