               op2 = 1 + dp[index+1][s[index]-'a'];
               else if(abs(s[index]-'a' - prev) <= k)
               op2 = 1 + dp[index+1][s[index]-'a'];

               dp[index][prev] = max(op1 , op2);
            }
        }

       return max(dp[0][s[0]-'a'] , dp[0][26]);
   }

   int SolveByTabSpaceOptimised(string &s , int &k )
   {
        vector<int> next(27, 0) ;
