        for(int i = 1; i <= s.size(); i++) {
            int x = stoi(s.substr(0, i)); // fixed the bug by changing parseInt to stoi
            if(valid(n, s.substr(i), sum + x)) {
                return true;
            }
        }

        // If no partition could add up to required sum n
