    ans[0] = 0;
    queue<pair<int,int>> visit;
    visit.push({0, 0});
    while (!visit.empty()) {
        auto node = visit.front();
        visit.pop();
        int i = node.first;
        int color = node.second;
        if (color <= 0) {
            for (auto j : redchilds[i]) {
                if (ans[j] == INT_MAX) {
                    ans[j] = ans[i] + 1;
                    visit.push({j, 1});
                }
            }
        }
        if (color >= 0) {
            for (auto j : bluechilds[i]) {
                if (ans[j] == INT_MAX) {
                    ans[j] = ans[i] + 1;
                    visit.push({j, -1});
                }
            }
        }
    }
    for (int i = 0; i < n; i++) {
        if (ans[i] == INT_MAX) ans[i] = -1;
    }
    return ans;
}

