        return task(root, false);
    }

    static int task(TreeNode root, boolean isLeft) {
        if(root == null) return 0;

        int sum = 0;

        if(isLeft && root.left==null && root.right==null) {
            sum += root.val;
        }

        sum += task(root.left, true);
        sum += task(root.right, false);

