import java.util.HashMap;

class Solution {
    public boolean checkSubarraySum(int[] nums, int k) {
        HashMap<Integer,Integer> hm  = new HashMap<>();
        hm.put(0,-1); // fix: initialize the map with sum = 0 and index = -1

        int sum = 0;
        for( int  i = 0;i < nums.length; i++){
            int num = nums[i];
