import java.util.*;

class Solution {
    public int[] frequencySort(int[] a) {
        Map<Integer, Integer> frequencyMap = new HashMap<>();

        for(int i=0; i<a.length; i++) {
            int num = a[i];
            frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1);
        }

        List<Integer> sortedNums = new ArrayList<>(frequencyMap.keySet());
        Collections.sort(sortedNums, (n1, n2) -> {
            int freq1 = frequencyMap.get(n1);
            int freq2 = frequencyMap.get(n2);

            if(freq1 != freq2) {
                return freq1 - freq2;
            } else {
                return n2 - n1;
            }
        });

        int[] sortedArr = new int[a.length];
        int index = 0;
        for(int num : sortedNums) {
            int freq = frequencyMap.get(num);
            while(freq > 0) {
                sortedArr[index++] = num;
                freq--;
            }
        }

        return sortedArr;
    }
}

