class Solution {
    public int maxNumberOfBalloons(String text) {
        final int[][] cache = new int[2][5];
        cache[0][0] = 97;  // a
        cache[0][1] = 98;  // b
        cache[0][2] = 108; // l
        cache[0][3] = 110; // n
        cache[0][4] = 111; // o

        // cacl letter frequencies
        for (char ch : text.toCharArray()) {
            final int intValue = ch;
            for (int i = 0; i < 5; i++) {
                if (intValue == cache[0][i]) {
                    cache[1][i]++;
                    break;
                }
            }
        }

