                    return 1;
                }
                else{
                    return -1;
                }

            }
        };
        PriorityQueue<pair> pq=new PriorityQueue<>(comp);
        for(int i=0;i<quiet.length;i++){
            ans[i]=-1;
            pq.add(new pair(i,quiet[i]));
