    {
        String prevStr = "1";
        for (int i = 2; i <= n; i++)
        {
            String cnsNum = convert(prevStr); //cns  = "count and say Num"
            prevStr = cnsNum; //remember (dp) the previous built string
        }
        return prevStr;


    }
}

