class Solution {
    public int maximumRequests(int n, int[][] requests) {
        int l = requests.length;
        for (int i = l; i >= 1; i--) {
            for (int[] j : combinations(requests, i)) {
                if (counter(j)) {
                    return i-1;
                }
            }
        }
        return 0;
    }

    private boolean counter(int[][] arr) {
        int[] diff = new int[arr.length];
        for (int i = 0; i < arr.length; i++) {
            diff[arr[i][0]]--;
            diff[arr[i][1]]++;
        }
        for (int d : diff) {
            if (d != 0) {
                return false;
            }
        }
        return true;
    }

    private List<int[]> combinations(int[][] arr, int len) {
        List<int[]> combinations = new ArrayList<>();
        backtrack(arr, combinations, new int[len], 0, 0);
        return combinations;
    }

    private void backtrack(int[][] arr, List<int[]> combinations, int[] tempList, int start, int count) {
        if (count == tempList.length) {
            int[][] temp = new int[tempList.length][2];
            for (int i = 0; i < tempList.length; i++) {
                temp[i] = arr[tempList[i]];
            }
            combinations.add(temp);
        } else {
            for (int i = start; i < arr.length; i++) {
                tempList[count] = i;
                backtrack(arr, combinations, tempList, i+1, count+1);
            }
        }
    }
}

