class Solution { 
  public int arraySign(int[] nums) {
    int z = 1; // Initialize z as 1 instead of 0
    for (int v : nums) {
      if (v == 0)
        return 0;
      else
        z *= v; // Use multiplication instead of xor operation
    }
    return z > 0 ? 1 : -1; // Use greater than operator to determine sign
  }
}

