class Solution {
    int index;
    public TreeNode buildTree(int[] inorder, int[] postorder) {
        // Build a hashmap to store the indices of the inorder array
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < inorder.length; i++) {
            map.put(inorder[i], i);
        }

        index = postorder.length - 1;

        return buildTree(0, inorder.length - 1, postorder, map);
    }

    private TreeNode buildTree(int start, int end, int[] postorder, Map<Integer, Integer> map) {
        // If start > end, there are no elements to construct the subtree
        if (start > end) {
            return null;
        }

        // Pick the last element from postorder as the root
        int rootValue = postorder[index];
        TreeNode root = new TreeNode(rootValue);
        index--;

        // Find the index of this root in the inorder array
        int rootIndex = map.get(rootValue);

        // Recursively build the right subtree
        root.right = buildTree(rootIndex + 1, end, postorder, map);

        // Recursively build the left subtree
        root.left = buildTree(start, rootIndex - 1, postorder, map);

        return root;
    }
}

