    class UFDS{

        int[] id;
        int[] size;
        int[] min;

        public UFDS(int N){

            id = new int[N];
            size = new int[N];
            min = new int[N];
            for(int i = 0; i < N; i++){
                id[i] = i;
                size[i] = 1;
                min[i] = i;
            }
