        int one=0, two=0;
        for(int i=0; i<numString.length(); i++){
            int n = Character.getNumericValue(numString.charAt(i));
            if(n%2==0){
                res += even.get(one);
                one++;
            }else{
                res += odd.get(two);
                two++;
            }
        }
        return Integer.parseInt(res);
    }
}

