        int val = postorder[index];
        index--;

        TreeNode root = new TreeNode(val);
        if(l == r) return root;

        int in = m.get(val);

        root.right = buildTree(in + 1, r, postorder, m);
        root.left = buildTree(l, in - 1, postorder, m);

        return root;
    }
}

