class Solution {
    double possible = 0.0;
    double valid = 0.0;

    public double getProbability(int[] balls) {
        int[] first = new int[balls.length];
        int[] second = new int[balls.length]; //initialize second array
        dfs(balls, first, second, 0, 0, 0);
        return valid / possible;
    }

    private void dfs(int[] balls, int[] first, int[] second, int num1, int num2, int idx) {
        if (idx == balls.length) {
            if (num1 == num2) {
                double distrib = permCalc(first) * permCalc(second); //change permFake method to permCalc method
                possible += distrib;

                if (unique(first) == unique(second)) {
                    valid += distrib;
                }
            }
            return;
        }

        for (int b = 0; b <= balls[idx]; b++) {
            first[idx] = b;
            second[idx] = balls[idx] - b;
            dfs (balls, first, second, num1 + first[idx], num2 + second[idx], idx + 1);
        }
    }

    private int factorial(int n){
        int ret = 1;
        while (n > 0) {
            ret *= n;
            n--;
        }
        return ret;
    }

    private double permCalc(int[] arr) { //modify permFake method to permCalc method and fix return type to double
        int sum = 0;
        int n = 0;
        for (int num : arr) {
            sum += num;
            n += factorial(num);
        }
        return factorial(sum) / n;
    }
}

