        if(grid[row][col] == -1) {
            used[row][col] = false;
            return 0;
        }

        int paths = 0;

        //DOWN
        if(row < grid.length - 1 && !used[row + 1][col]) {
            paths += count(grid, used, row + 1, col, countZeros - 1);
        }
        //RIGHT
        if(col < grid[0].length - 1 && !used[row][col + 1]) {
