class Solution {
    public String reverseWords(String s) {
        StringBuilder ans = new StringBuilder();
        int i = s.length() - 1;
        while(i >= 0) {
            if(s.charAt(i) == ' ') {
                i--;
                continue;
            }
            int start = -1;
            int y = i - 1;
            while(y >= 0) {
                if(s.charAt(y) == ' ') {
                    start = y;
                    break;
                }
                y--;
            }
            ans.append(s.substring(start + 1, i + 1)).append(" ");
            i = start;
        }
        if(ans.length() > 0) {
            ans.deleteCharAt(ans.length() - 1);
        }
        return ans.toString();
    }
}

