public class Solution {
    public int countNegatives(int[][] grid) {
        int ROWS = grid.length;
        int COLS = grid[0].length;
        int negatives = 0;
        for (int row = ROWS - 1; row >= 0; row--) {
            int col = 0;
            while (col < COLS && grid[row][col] >= 0) {
                col++;
            }
            negatives += COLS - col;
        }
        return negatives;
    }
}

