class Solution {
    public List<List<String>> groupStrings(String[] words) {
        Map<String, List<String>> groups = new HashMap<>();
        for (String word : words) {
            String key = getKey(word); // get the key for the word
            if (!groups.containsKey(key)) {
                groups.put(key, new ArrayList<>()); // create a new group if key doesn't exist
            }
            groups.get(key).add(word); // add the word to its respective group
        }
        return new ArrayList<>(groups.values());
    }

    private String getKey(String word) {
        if (word.length() == 0) return ""; // if word is empty, return empty string
        StringBuilder sb = new StringBuilder();
        int offset = word.charAt(0) - 'a'; // calculate the offset for the key
        for (int i = 0; i < word.length(); i++) {
            char ch = (char) (word.charAt(i) - offset); // subtract the offset from each character
            if (ch < 'a') ch += 26; // if character becomes less than 'a', wrap it around to the end of the alphabet
            sb.append(ch);
        }
        return sb.toString();
    }
}

