class Solution { 
    public int makeArrayIncreasing(int[] arr1, int[] arr2) {
        // Sorting arr2 in ascending order
        Arrays.sort(arr2);

        // Calling helper function to find the minimum number of operations
        int answer = helper(0, -1, arr1, arr2);

        // Returning the answer, handling the case when no feasible solution found
        return answer < Integer.MAX_VALUE-100 ? answer : -1;
    }

    // Creating a HashMap to store computed results for optimization
    Map<Pair<Integer, Integer>, Integer> dp = new HashMap<>();

    // Recursive helper function to compute the minimum number of operations
    private int helper(int i, int prev, int[] arr1, int[] arr2) {
        // Base case, when the traversal reaches the end of arr1
        if (i == arr1.length) {
            return 0;
        }

        // Check if the result for given parameters is already computed
        if (dp.containsKey(new Pair<>(i, prev))) {
            return dp.get(new Pair<>(i, prev));
        }

        // Initialize operation as a large value
        int operation = Integer.MAX_VALUE-100;

        // Check if current element in arr1 is greater than the previous element
        if (arr1[i] > prev) {
            // Recursively call the helper function with the next index, current element as previous,
            // and arr1 and arr2 as input arrays to compute the number of operations
            operation = helper(i + 1, arr1[i], arr1, arr2);
        }

        // Find the index in arr2 where the element is greater than the previous element
        int idx = binarySearch(arr2, prev);

        // Check if the index exists
        if (idx < arr2.length) {
            // Recursively call the helper function with the next index, current element as previous,
            // and arr1 and arr2 as input arrays to compute the number of operations
            operation = Math.min(operation, 1 + helper(i + 1, arr2[idx], arr1, arr2));
        }

        // Store the computed result to avoid recomputation
        dp.put(new Pair<>(i, prev), operation);
        return operation;
    }

    // Binary search function to find the index in arr2 where the element is greater than the value
    private static int binarySearch(int[] arr, int value) {
        int left = 0, right = arr.length-1;
        while (left <= right) {
            int mid = left+(right-left) / 2;
            if (arr[mid] <= value) {
                left = mid + 1;
            } else {
                right = mid-1;
            }
        }
        return left;
    } 
}

