class Solution {
    public long countOperationsToEmptyArray(int[] a) {
        HashMap<Integer, Integer> pos = new HashMap<>();
        long n = a.length, res = n;
        for (int i = 0; i < n; i++)
            pos.put(a[i], i);
        Arrays.sort(a);
        for (int i = 1; i < n; i++)
            if (pos.get(a[i]) < pos.get(a[i - 1]))
                res += n - i;
        return res;
    }
}

