        for (int i = 0; i < nums.length; i++) {
            sum += nums[i];
            if (k != 0) {
                sum %= k; 
            }
            if (sum == 0 && i > 0) {
                return true;
            }
            if (map.containsKey(sum) && i - map.get(sum) > 1) {
                return true;
            }

            if (!map.containsKey(sum)) {
                map.put(sum, i); 
            }
        }
