		reverse(n,i+1,n.length-1);

		for(int p=0;p<n.length;p++) {
			System.out.println(n[p]);
		}

	}
	public static void swap(int n[],int i,int j) {
		int temp=n[i];
		n[i]=n[j];
		n[j]=temp;
	}
	public static void reverse(int n[],int i,int j) {
		while(i<j) {
			swap(n,i,j);
			i++;
			j--;
		}
	}
}

