        if(idx==nums.length){
            if(op.size()>1){
                set.add(op);
            }
            return;
        }
        if(prev==-1 || nums[idx]>=nums[prev]){
            List<Integer>op1=new ArrayList<>(op);
            op1.add(nums[idx]);
            solve(nums,idx,op1,idx+1);
        }
        solve(nums,prev,op,idx+1);

