    public List<List<Integer>> findSubsequences(int[] nums) {
        List<Integer>op=new ArrayList<>();
        solve(nums,-1,op, 0);
        Iterator<List<Integer>> it = set.iterator();
        while(it.hasNext())vec.add(it.next());
        return vec;
    }
}

