            else if(i!=0 && i!=street.length()-1 && street.charAt(i-1)=='H' && street.charAt(i+1)=='H' && !set.contains(i-1) && !set.contains(i+1))
            {
                set.add(i-1);
                set.add(i+1);
                count++;
            }
        }
        for(int i=0;i<street.length();i++)
        {
            if(street.charAt(i)=='H' && !set.contains(i))
                count++;
        }
        return count;
    }
}

