    def isTransformable(self, s: str, t: str) -> bool:
        if Counter(s)!=Counter(t):
            return False
        ind = defaultdict(deque)
        for id, i in enumerate(t):
            ind[i].append(id)
        a = []
        for i in s:
            a.append(ind[i].popleft())
        n=len(a)
