            losers[match[1]] += 1

        res_1, res_2 = [], []

        for k, v in winners.items():
            if k not in losers:
                res_1.append(k)

        for k, v in winners.items(): 
            if v == 1:
                res_2.append(k)

        res_1.sort()
        res_2.sort()

        return [ res_1, res_2 ]

