from collections import deque

class Solution:
    def levelOrderBottom(self, root: Optional[TreeNode]) -> List[List[int]]:
        if not root:
            return []

        queue = deque()
        queue.append(root)
        levels = []

        while queue:
            current_level = []
            size = len(queue)

            for _ in range(size): 
                tmp = queue.popleft()
                current_level.append(tmp.val)

                if tmp.left:
                    queue.append(tmp.left)
                if tmp.right:
                    queue.append(tmp.right)

            levels.append(current_level)

        return levels[::-1]

