    def minDistance(self, word1: str, word2: str) -> int:
        n ,m = len(word1),len(word2)
        #converting word1 to word2 
        @lru_cache(None)
        def rec(i,j):
            if(i==n and j==m): return 0;
            elif(i==n):        return m-j
            elif(j==m):        return n-i
            elif(word1[i]==word2[j]):
                 return rec(i+1,j+1)
            else:
