        dp_row = [[False for _ in range(len(matrix[0]))] for _ in range(len(matrix))]
        return self.helper(matrix, dp_row, dp_col)

    def helper(self, matrix, dp_row, dp_col):
        for i in range(len(matrix)):
            for j in range(len(matrix[0])):
                if dp_row[i][j] or dp_col[i][j]:
                    return False
                dp_row[i][j] = True
                dp_col[i][j] = True
        return True

class Solution:
    def nthUglyNumber(self, n: int, a: int, b: int, c: int) -> int:
        def gcd(x, y):
            while y:
                x, y = y, x % y
            return x

        ab = a*b//gcd(a, b)
        bc = b*c//gcd(b, c)
        ca = c*a//gcd(c, a)
        abc = ab*c//gcd(ab, c)

        lo, hi = 1, n*min(a, b, c)
        while lo < hi: 
            mid = lo + (hi - lo) // 2
            if mid//ab + mid//bc + mid//ca - mid//a - mid//b - mid//c + mid//abc < n:
                lo = mid + 1 
            else:
                hi = mid 
        return lo

