class Solution:
    def fib(self, n: int) -> int:
        Fib = [-1 for _ in range(n+1)]
        if n == 0:
            return 0
        if n == 1:
            return 1
        Fib[0] = 0
        Fib[1] = 1

        return self.helper(n, Fib)

    def helper(self, n, Fib):
        if Fib[n] != -1:
            return Fib[n]
        Fib[n] = self.helper(n-1, Fib) + self.helper(n-2, Fib)
        return Fib[n]

