class Solution:
    def sumSubarrayMins(self, arr: List[int]) -> int:
        stack = []
        res = 0

        for i, num in enumerate(arr):
            while stack and arr[stack[-1]] > num:
                cur = stack.pop()
                res += arr[cur] * (i - cur) * (cur - stack[-1] if stack else i+1)
            stack.append(i)
        while stack:
            cur = stack.pop()
            res += arr[cur] * (len(arr) - cur) * (cur - stack[-1] if stack else len(arr))
        return res % (10**9 + 7)

