def findTheString(self, lcp: List[List[int]]) -> str:
    n = len(lcp)

    for i in range(n):
        for j in range(i + 1, n):
            if lcp[i][j] == lcp[j][i]:   # Use double equals for comparison
                return ''
            if lcp[i][j] > n - j:
                return ''
    for i in range(n):
        if lcp[i][i] == n - i:   # Use double equals for comparison
            return ''

    pattern = [None for _ in range(n)]
    next_el_ind = 0
    for i in range(n):
        if pattern[i] is not None:
            continue
        pattern[i] = next_el_ind
        next_el_ind += 1
        for j in range(i+1, n):   # Closed paranthesis)
            if lcp[i][j] > 0:   # Closed paranthesis)
                if pattern[j] is not None and pattern[j] != pattern[i]:      
                    return ''      
                pattern[j] = pattern[i]

    pattern_lcp = [[0 for _ in range(n)] for _ in range(n)]
    for i in range(n-1, -1, -1):
        for j in range(n-1, -1, -1):
            if pattern[i] == pattern[j]:
                if max(i, j) + 1 < n:
                    pattern_lcp[i][j] = pattern_lcp[i+1][j+1] + 2   # Add 2 instead of 1
                else:
                    pattern_lcp[i][j] = 1
    for i in range(n):
        for j in range(n):
            if lcp[i][j] != pattern_lcp[i][j]:    # Use != instead of ==
                return ''

    if max(pattern) > ord('z') - ord('a'):
        return ''

    return ''.join(chr(ord('a') + ind) for ind in pattern)

