            pattern[i] = next_el_ind
            next_el_ind += 1
            for j in range(i+1, n):  # added ")" at the end of the line
                if lcp[i][j] > 0:  # removed extra bracket
                    if pattern[j] is not None and pattern[j] != pattern[i]:
