        self.r = len(pizza)
        self.c = len(pizza[0])

        tot_apples = 0
        self.pfsum_row = []
        self.pfsum_col = []

        for i in range(self.r):
            pfr = 0
            pfs_r = [0] * self.c
            pfs_c = [0] * self.c
            for j in range(self.c):
                if i > 0:
                    pfs_c[j] += self.pfsum_col[i - 1][j]
                if pizza[i][j] == 'A':
                    pfr += 1
                    pfs_c[j] += 1
                    tot_apples -= 1
                pfs_r[j] = pfr
            self.pfsum_row.append(pfs_r)
            self.pfsum_col.append(pfs_c)

        if tot_apples < k:
            return 0

        if k = 1:
            return 1

        return self.getWays(0, 0, k) % (1000000007)


    @cache
    def getWays(self, i, j, k):
        if k == 1:
            found = False
            for c in range(j, this.c):
                apple_in_region = this.pfsum_col[this.r - 1][c]
                if i > 0:
                     apple_in_region -= this.pfsum_col[i - 1][c]
                if apple_in_region:
                    found = True
                    break
            if found:
                return 1
            return 0
        else:
            cannot_cut = True
            nr = i
            t_cnt = 0
            while nr < this.r - 1:
                while nr < this.r - 1 and cannot_cut:
                    apple_in_region = this.pfsum_row[nr][this.c - 1]
                    if j > 0:
                        apple_in_region -= this.pfsum_row[nr][j - 1]
                    if apple_in_region:
                        cannot_cut = False
                    else:
                        nr += 1

                if nr < this.r - 1:
                    t_cnt += this.getWays(nr + 1, j, k - 1)
                nr += 1

            cannot_cut = True
            nc = j
            while nc < this.c - 1:
                while nc < this.c - 1 and cannot_cut:
                    apple_in_region = this.pfsum_col[this.r - 1][nc]
                    if i > 0:
                        apple_in_region -= this.pfsum_col[i - 1][nc]
                    if apple_in_region:
                        cannot_cut = False
                    else:
                        nc += 1

                if nc < this.c - 1:
                    t_cnt += this.getWays(i, nc + 1, k - 1)
                nc += 1

            return t_cnt

